INSERT INTO `news` (`news_id`, `news_data`, `news_tresc`) VALUES
(12, '2016-12-31 23:00:00', 'Start systemu ! Witaj w naszej aplikacji!'),
(14, '2017-01-22 20:20:20', 'Witamy w aplikacji Kliniki Weterynaryjnej "Kotus Marcelus". Kliknij ikonę w lewym górnym rogu, aby wybrać opcję z menu administracyjnego. \r\nW związku z ciągłym rozwojem aplikacji, w razie znalezienia błedów i nieodpowiedniego działania aplikacji, prosimy o kontakt poprzez zakładkę Zgłoszenia.'),
(15, '2017-01-22 20:28:46', 'Specjalistyczna przychodnia weterynaryjna oferująca kompleksowy pakiet usług dla psów i kotów. Nasz zespół tworzą doświadczeni lekarze różnych specjalizacji. Zapraszamy również do naszego sklepu zoologicznego a także do gabinetu kosmetyki zwierząt (kąpiel, strzyżenie, czesanie). '),
(16, '2017-01-22 20:29:17', 'Dysponujemy nowoczesnym sprzętem diagnostycznym (RTG, EKG, USG Doppler, echo serca, laser terapeutyczny, aparatura do badań morfologii i&nbsp;biochemii). Wykonujemy zabiegi chirurgiczne (chirurgia miękka i&nbsp;ortopedia), okulistyczne, rehabilitacyjne szczepienia oraz profilaktykę. '),
(17, '2017-01-22 20:32:35', 'Nasi podopieczni to m. In. Laureaci prestiżowych konkursów zwierząt rasowych w tym rownież psów pracujących. ');
